import PasswordInput, { PasswordInputWC } from './password-input';
import SearchInput, { SearchInputWC } from './search-input';
import TextInput, { TextInputWC } from './text-input';
import TextareaInput, { TextareaInputWC } from './textarea-input';

export default class Form {
  static TextInput: object = TextInput;
  static SearchInput: object = SearchInput;
  static PasswordInput: object = PasswordInput;
  static TextareaInput: object = TextareaInput;

  static init(optionsPassword = {}, optionsSearch = {}, optionsText = {}, optionsTextarea = {}): void {
    PasswordInput.init(optionsPassword);
    SearchInput.init(optionsSearch);
    TextInput.init(optionsText);
    TextareaInput.init(optionsTextarea);
  }
}

export class FormWC {
  static init(): void {
    PasswordInputWC.init();
    SearchInputWC.init();
    TextInputWC.init();
    TextareaInputWC.init();
  }
}
